<script>
import DynamicFieldSettings from "./DynamicFieldSettings.vue";
import { mapState } from "vuex";

export default {
  props: ["info", "isDetailSelected"],
  components: {
    DynamicFieldSettings,
  },
  data() {
    return {
      editedInfo: {},
    };
  },
  created() {
    console.log("Info prop in EditInformationForm:", this.info);
    if (this.info) {
      this.editedInfo = {
        ...this.info,
        id: this.info.id || this.generateNewId(),
      };
    }
  },

  watch: {
    info(newVal) {
      console.log("New info:", newVal);
      this.editedInfo = { ...newVal };
    },
    isDetailSelected(newVal, oldVal) {
      console.log("isDetailSelected changed from", oldVal, "to", newVal);
    },
  },
  computed: {
    ...mapState(["fields", "information"]),
    fields() {
      return this.$store.state.fields;
    },
    shouldShowNoDetails() {
      return !this.isDetailSelected;
    },
  },
  methods: {
    generateNewId() {
      return Math.random().toString(36).substr(2, 9);
    },
    initializeEditedInfo() {
      this.fields.forEach((field) => {
        this.$set(this.editedInfo, field.name, "");
      });
    },
    updateInformation() {
      console.log("Full editedInfo object:", this.editedInfo);

      const existingIds = this.information.map((info) => info.id);
      if (existingIds.includes(this.editedInfo.id)) {
        console.log("Updating existing item:", this.editedInfo);
        this.$store.commit("updateInformation", this.editedInfo);
      } else {
        console.log("Adding new item:", this.editedInfo);
        this.$store.commit("addNewInformation", this.editedInfo);
      }

      this.$emit("handleUpdateInfo", this.editedInfo);
    },
    removeInformation() {
      // Emit an event to the parent component to remove this information block
      this.$emit("handleRemoveInfo", this.editedInfo.id);
    },
  },
};
</script>
